import "./App.css";
import { useState, useEffect } from 'react';
import Navbar from "./Components/Navbar";
import Landing from "./Components/Landing";
import AboutView from "./Components/AboutView";
import { Routes, Route } from "react-router-dom";
import SearchView from "./Components/SearchView";
function App() {

  const [searchResults, setSearchResults] = useState([]);
  const [searchText, setSearchText] = useState('');

  useEffect (()=>{
    if(searchText) {
    fetch(`https://api.themoviedb.org/3/search/movie?api_key=ee21ca5b2c7513e4b1a894b78e0d071a&language=en-US&query=${searchText}&page=1&include_adult=false`)
    .then(response => response.json())
    .then(data => {
      setSearchResults(data.results)
    })
  }
}, [searchText])

  return (
    <div>
      <Navbar searchText={searchText} setSearchText={setSearchText}/>
      <Routes>
        <Route index element={<Landing />} />
        <Route path="/Landing" element={<Landing />} />
        <Route path="/AboutView" element={<AboutView />} />
        <Route path="/SearchView" element={<SearchView keyword={searchText} searchResults={searchResults} />} />  

      </Routes>
    </div>
  );
}

export default App;
