import "./App.css";
import { useState, useEffect } from 'react';
import Navbar from "./Components/Navbar";
import Landing from "./Components/Landing";
import AboutView from "./Components/AboutView";
import { Switch, Route } from "react-router-dom";
import SearchView from "./Components/SearchView";
import MovieView from "./Components/MovieView";
function App() {

  const [searchResults, setSearchResults] = useState([]);
  const [searchText, setSearchText] = useState('');

  useEffect (()=>{
    if(searchText) {
      //live_U6C4TGqMpRKnmiak3ebzG1oRW3eApNjQkpHu307G8eGZ1BsgXWdO9oeuisSOuDuQ
      //https://api.thecatapi.com/v1/images/search?limit=11&api_key
      fetch(`https://api.themoviedb.org/3/search/movie?api_key=ee21ca5b2c7513e4b1a894b78e0d071a&include_adult=false&language=en-US&query=${searchText}`)

    .then(response => response.json())
    .then(data => {
      setSearchResults(data.results)
    })
  }
}, [searchText])

  return (
    <div>
    <Navbar searchText={searchText} setSearchText={setSearchText} />
    <Switch>
      <Route path="/" exact>
        <Home />
      </Route>
      <Route path="/about" component={AboutView} />
      <Route path="/search">
        <SearchView keyword={searchText} searchResults={searchResults} />
      </Route>
      <Route path="/movies/:id" component={MovieView} />
    </Switch>
  </div>
  );
}

export default App;
