import Hero from "./Hero";
import { Link } from 'react-router-dom';

const MovieCard = ({ movie }) => {
  //const posterUrl = `https://image.tmdb.org/t/p/w500${movie.poster_path}`
  const posterUrl = `https://api.thecatapi.com/v1/images/search?limit=10&breed_ids=beng&api_key=live_U6C4TGqMpRKnmiak3ebzG1oRW3eApNjQkpHu307G8eGZ1BsgXWdO9oeuisSOuDuQ`

  const detailUrl = `/movies/${movie.id}`
  return (
    <div className="col-lg-3 col-md-3 col-2 my-4">
      <div className="card">
        <img src={posterUrl} className="card-img-top" alt={movie.original_title} />
        <div className="card-body">
          <h5 className="card-title">{movie.original_title}</h5>
          <Link to={detailUrl} className="btn btn-primary">Show details</Link>
        </div>
      </div>
    </div>
  )
}
// my api key ee21ca5b2c7513e4b1a894b78e0d071a
// the url https://api.themoviedb.org/3/movie/550?api_key=ee21ca5b2c7513e4b1a894b78e0d071a
const SearchView = ({keyword, searchResults}) => {
    const title = `You are searching for ${keyword}`

    const resultsHtml = searchResults.map((obj, i)=>{
      return <MovieCard movie={obj} key={i} />
    })
    return (
      <>
        <Hero text={title} />
        {resultsHtml &&
          <div className="container">
            <div className="row">
              {resultsHtml}
            </div>
          </div>
        }
      </>
    );
  };
  
  export default SearchView;
